mapper.select:
select
	*
from
	member
where
	mb_no = #{cus.mb_no}
	and mb_id = #{cus.mb_id}
	and mb_name = #{cus.mb_name}
	and mb_password = #{cus.mb_password}
	and mb_email = #{cus.mb_email}
	and mb_is_admin = #{cus.mb_is_admin}

mapper.delete:
delete from member
where
	mb_no = #{cus.mb_no}
	and mb_id = #{cus.mb_id}
	and mb_name = #{cus.mb_name}
	and mb_password = #{cus.mb_password}
	and mb_email = #{cus.mb_email}
	and mb_is_admin = #{cus.mb_mb_is_admin}
