mapper.select:

select * from reservation
where
	rv_start_date < #{rv_start_date}
and	
	rv_end_date > #{rv_end_date}


select * from reservation
where
	rv_start_date >= #{rv_start_date}
or	
	rv_end_date <= #{rv_end_date}


select * from reservation
where
	rv_room_num = #{rv_room_num}
and
	rv_start_date <= #{rv_start_date}
and	
	rv_end_date >= #{rv_end_date}
